import React from 'react';

class Square extends React.Component {

  render() {
    return (
      <button className="square" style={{background: this.props.color}}>
      </button>
    );
  }
}

class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      posX: 0,
      posY: 0,
    };
  }

  renderSquare(i,j, color = "#fff") {
    return <Square key ={[i,j].toString()} color = {color} />
  }

  render() {
    const playground = new Array()
    for(let i = 0; i < 10; i++){
      const row = new Array()
      for(let j = 0; j < 10; j++){
        if(this.state.posX == i && this.state.posY == j){
          row.push(this.renderSquare(i,j,"#060606"))
          continue;
        }
        row.push(this.renderSquare(i,j))
      }
      const newRow =  <div className="board-row">{row}</div>
      playground.push(newRow)
    }

    return (
      <div>
        {playground}
      </div>
    );
  }
}

class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
      </div>
    );
  }
}


function App() {

  return (
    <Board />
  )
} 

export default App;
