import React from 'react';

class Square extends React.Component {
  render() {
    return (
      <button className="square">
      </button>
    );
  }
}

class Test extends React.Component{
  renderSquare(i) {
    return <Square key = {i}/>
  }

  render() {
    const row = new Array();
    for(let i = 0 ; i < 10; i++){
      row.push(this.renderSquare(i));
    }

    return (
      <div className="board-row">
      {row}
      </div>
    );
  }
}

