import React from 'react';

class Square extends React.Component {
  render() {
    return (
      <button className="square">
      </button>
    );
  }
}

class Test extends React.Component{
  renderSquare(i) {
    return <Square key = {i}/>
  }

  render() {
    const row = new Array();
    for(let i = 0 ; i < 10; i++){
      row.push(this.renderSquare(i));
    }

    return (
      <div className="board-row">
      {row}
      </div>
    );
  }
}

class Board extends React.Component {
  renderSquare(i,j) {
    return <Square key ={[i,j].toString()}/>
  }

  render() {
    const playground = new Array()
    for(let i = 0; i < 10; i++){
      const row = new Array()
      for(let j = 0; j < 10; j++){
        row.push(this.renderSquare(i,j))
      }
      playground.push(row)
    }

    return (
      <div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}

