import React from 'react';


class Navbar extends React.Component{
  render(){
    return <div className = "navbar"></div>
  }
}

class Main extends React.Component{
  render(){
    return <div id = "main"></div>
  }
}

class Main1 extends React.Component{
  render(){
    return <div id = "main1" onMouseOver={()=>this.props.onMouseOver()} onMouseLeave={()=>this.props.onMouseLeave()} style={this.props.style1}>
      <span id = "main1text" style={this.props.styleText}>"How are you?"</span>
      </div>
  }
}

class Main2 extends React.Component{
  render(){
    return <div id = "main2" onMouseOver={()=>this.props.onMouseOver()} onMouseLeave={()=>this.props.onMouseLeave()} style={this.props.style2}>
      <span id = "main2text" style={this.props.styleText}>"How are you?"</span>
      </div>
  }
}

class Main3 extends React.Component{
  render(){
    return <div id = "main3" onMouseOver={()=>this.props.onMouseOver()} onMouseLeave={()=>this.props.onMouseLeave()} style={this.props.style3}>
      <span id = "main3text" style={this.props.styleText}>"How are you?"</span>
      </div>
  }
}

class Sidebar extends React.Component{
  render(){
    return <div className = "sidebar"></div>
  }
}

class First extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      origin:true,
      style1:{},
      style2:{},
      style3:{},
      styleText:{},
    };
  }

  changeStyle = (i)=>{
    var trans = 'scale(1)';
    var style ="style"+i.toString()
    this.setState({[style]: {transform:trans}, styleText:{opacity:1},origin:false});
    for(var j=1;j<4;j++){
      if(j!==i){
        var otherStyle ="style"+j.toString()
        this.setState({[otherStyle]:{opacity:0}})
      }
    }
  }

  resetStyle =(i)=>{
    var trans = 'scale(1.25) rotate(36.85deg) translate(0,'+(i*5-5).toString()+'vw)';
    var style ="style"+i.toString()
    this.setState({[style]: {transform:trans}, styleText:{},origin:true});
  }

  handleMouseOver(i){
    this.timeID = setTimeout(this.changeStyle, 1000,i);
  }

  handleMouseLeave(i){
    clearTimeout(this.timeID);
    if(!this.state.origin){
      this.resetStyle(i);
    }
  }

  render(){
    return <div className = "first">
      <Navbar />
      <Main />
      <Main1 onMouseOver={()=>this.handleMouseOver(1)} onMouseLeave={()=>this.handleMouseLeave(1)} style1={this.state.style1} styleText={this.state.styleText}/>
      <Main2 onMouseOver={()=>this.handleMouseOver(2)} onMouseLeave={()=>this.handleMouseLeave(2)} style2={this.state.style2} styleText={this.state.styleText}/>
      <Main3 onMouseOver={()=>this.handleMouseOver(3)} onMouseLeave={()=>this.handleMouseLeave(3)} style3={this.state.style3} styleText={this.state.styleText}/>
      <Sidebar />
    </div>
  }
}

class Second extends React.Component{
  render(){
  return <div className = "second"></div>
  }
}

function App() {

  return (
    <div>
    <First />
    <Second />
    </div>
  )
} 

export default App;
