import React from 'react';

class Square extends React.Component {

  render() {
    return (
      <button className="square" style={{background: this.props.color}}>
      </button>
    );
  }
}

class Board extends React.Component {

  renderSquare(i,j, color = "#fff") {
    return <Square key ={[i,j].toString()} color = {color} />
  }

  render() {
    const playground = []
    for(let i = 0; i < 10; i++){
      const row = []
      for(let j = 0; j < 10; j++){
        if(this.props.posX === i && this.props.posY === j){
          row.push(this.renderSquare(i,j,"#060606"))
          continue;
        }
        row.push(this.renderSquare(i,j))
      }
      const newRow =  <div className="board-row" key = {i}>{row}</div>
      playground.push(newRow)
    }

    return (
      <div>
        {playground}
      </div>
    );
  }
}

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      posX: 0,
      posY: 0,
    };
  }

  handleKeyPress = (event) => {
    if(event.key === "ArrowDown"){
      this.setState((state) => ({
        counter: state.counter + props.increment
      }))
    }
  }
  componentDidMount(){
    document.addEventListener("keydown", this.handleKeyPress, false);
  }

  componentWillUnmount(){
    document.removeEventListener("keydown", this.handleKeyPress, false);
  }

  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board posX={this.state.posX} posY = {this.state.posY}/>
        </div>
      </div>
    );
  }
}


function App() {

  return (
    <Game />
  )
} 

export default App;
