import React from 'react';

class Square extends React.Component {

  render() {
    return (
      <button className="square" style={{background: this.props.color}}>
      </button>
    );
  }
}

class Board extends React.Component {

  renderSquare(i,j, color = "#fff") {
    return <Square key ={[i,j].toString()} color = {color} />
  }

  render() {
    const playground = [];
    var pos = 0;
    var len = this.props.len;
    for(let i = 0; i < len; i++){

      const row = [];

      for(let j = 0; j < 10; j++){
        if(this.props.pos === pos){
          row.push(this.renderSquare(i,j,"#060606"))
          pos+=1;
          continue;
        }
        row.push(this.renderSquare(i,j))
        pos+=1;
      }
      const newRow =  <div className="board-row" key = {i}>{row}</div>
      playground.push(newRow)
    }

    return (
      <div>
        {playground}
      </div>
    );
  }
}

class Control extends React.Component{
  render() {
    return (
      <button className="control" onClick={() => this.props.onClick()}>
      </button>
    );
  }
}

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      len: 10,
      pos: 0,
      snake: [0],
      food:undefined,
    };
  }

  generateFood(){
    var food = undefined;
    do{
      food = Math.floor(Math.random() * pow(10,2))
    }
      while(food in snake);

  }

  handleKeyPress = (event) => {
    var addition = this.state.len
    switch (event.key) {
    case "ArrowDown":
      this.setState((state) => ({
        pos: state.pos+addition
      }))
      break;
    case "ArrowUp":
      this.setState((state) => ({
        pos: state.pos-addition
      }))
      break;
    case "ArrowLeft":
      this.setState((state) => ({
        pos: state.pos-1
      }))
      break;
    case "ArrowRight":
      this.setState((state) => ({
        pos: state.pos+1
      }))
      break;
    default:
      break;
    }
  }

  componentDidMount(){
    document.addEventListener("keydown", this.handleKeyPress, false);
  }

  componentWillUnmount(){
    document.removeEventListener("keydown", this.handleKeyPress, false);
  }

  reset(){
    this.setState({pos: 0,})
  }

  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board pos={this.state.pos} len ={this.state.len}/>
        </div>
        <div className="side-board">
          <h2 className="title"> Reset game </h2>
          <Control onClick={()=>this.reset()}/>
        </div>
      </div>
    );
  }
}


function App() {

  return (
    <Game />
  )
} 

export default App;
