import React from 'react';

class Square extends React.Component {

  render() {
    return (
      <button className="square" style={{background: this.props.color}}>
      </button>
    );
  }
}

class Board extends React.Component {

  renderSquare(i,j, color = "#fff") {
    return <Square key ={[i,j].toString()} color = {color} />
  }

  render() {
    const playground = [];
    var pos = 0;
    var len = this.props.len;
    for(let i = 0; i < len; i++){

      const row = [];

      for(let j = 0; j < 10; j++){
        if(this.props.snake.includes(pos) || this.props.food === pos){
          row.push(this.renderSquare(i,j,"#060606"))
          pos+=1;
          continue;
        }
        row.push(this.renderSquare(i,j))
        pos+=1;
      }
      const newRow =  <div className="board-row" key = {i}>{row}</div>
      playground.push(newRow)
    }

    return (
      <div>
        {playground}
      </div>
    );
  }
}

class Control extends React.Component{
  render() {
    return (
      <button className="control" onClick={() => this.props.onClick()}>
      </button>
    );
  }
}

function Warning(props){
  if(props.game){
    return null;
  }
  return <h1 className="warning">Your game ends!</h1>
}

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      len: 10,
      snake: [0],
      food:undefined,
      game: true,
    };
  }

  generateFood(){
    var newFood = undefined;
    do{
      newFood = Math.floor(Math.random() * Math.pow(10,2))
    }
      while(newFood in this.state.snake);
      this.setState(({
        food: newFood
      })) 
  }

  eatFood(food){
    var newSnake = this.state.snake.slice();
    newSnake.unshift(food);
    this.setState({snake: newSnake})
  }

  handleKeyPress = (event) => {
    var addition = this.state.len
    var newPos = undefined;
    var food =this.state.food;
    switch (event.key) {
    case "ArrowDown":
      newPos = this.state.snake[0]+addition;
      break;
    case "ArrowUp":
      newPos = this.state.snake[0]-addition;
      break;
    case "ArrowLeft":
      newPos = this.state.snake[0]-1; 
      break;
    case "ArrowRight":
      newPos = this.state.snake[0]+1;
      break;
    default:
      break;
    }
    if (newPos === food){
      this.eatFood(food);
      this.generateFood();
      return;
    }
    if (newPos < 0 || newPos > Math.pow(10,2)-1){
      this.setState({game:false})
    }
    var newSnake = this.state.snake.slice();
    newSnake.unshift(newPos);
    newSnake.pop();
    this.setState({snake: newSnake})
  }

  componentDidMount(){
    this.generateFood();
    document.addEventListener("keydown", this.handleKeyPress, false);
  }

  componentWillUnmount(){
    document.removeEventListener("keydown", this.handleKeyPress, false);
  }

  reset(){
    this.setState({snake: [0],})
  }

  render() {
    if(!this.state.game){
        var boardStyle={opacity:0.7};
    }
    return (
      <div className="game">
        <div className="game-board">
          <Warning game={this.state.game}/>
          <Board snake={this.state.snake} len ={this.state.len} food={this.state.food} style={boardStyle}/>
        </div>
        <div className="side-board">
          <h2 className="title"> Reset game </h2>
          <Control onClick={()=>this.reset()}/>
        </div>
      </div>
    );
  }
}


function App() {

  return (
    <Game />
  )
} 

export default App;
