import React from 'react';


class Navbar extends React.Component{
  render(){
    return <div className = "navbar"></div>
  }
}

class Main extends React.Component{
  render(){
    return <div id = "main"></div>
  }
}

class Main1 extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      style1:{},
      style2:{opacity:0, transition:"opacity 3s"},
      content:undefined,
    };
  }

  changeStyle = ()=>{
    var trans = 'scale(1)';
    this.setState({style1: {transform:trans, zIndex:5},content:"How are you?", style2:{opacity:1, transition:"opacity 3s"}})
    ;}


  handleMouseOver(){
    this.timeID = setTimeout(this.changeStyle, 2000);
  }

  handleMouseLeave(){
    clearTimeout(this.timeID);
  }

  render(){
    return <div id = "main1" onMouseOver={()=>this.handleMouseOver()} onMouseLeave={()=>this.handleMouseLeave()} style={this.state.style1}>
      <span id = "main1text" style2={this.state.style2}>{this.state.content}</span>
      </div>
  }
}

class Main2 extends React.Component{
  render(){
    return <div id = "main2" ></div>
  }
}

class Main3 extends React.Component{
  // translate(5vw)
  render(){
    // const main2Style ={
    //   backgroundColor: "green",
    // }
    return <div id = "main3"></div>
  }
}

class Sidebar extends React.Component{
  render(){
    return <div className = "sidebar"></div>
  }
}

class First extends React.Component{
  render(){
    return <div className = "first">
      <Navbar />
      <Main />
      <Main1 />
      <Main2 />
      <Main3 />
      <Sidebar />
    </div>
  }
}

class Second extends React.Component{
  render(){
  return <div className = "second"></div>
  }
}

function App() {

  return (
    <div>
    <First />
    <Second />
    </div>
  )
} 

export default App;
