import React from 'react';


class Navbar extends React.Component{
  render(){
    return <div className = "navbar"></div>
  }
}

class Main extends React.Component{
  render(){
    return <div id = "main"></div>
  }
}

class Main1 extends React.Component{
  render(){
    return <div id = "main1"></div>
  }
}

class Main2 extends React.Component{
  render(){
    return <div id = "main2" onMouseOver={()=>this.props.onhover()}></div>
  }
}

class Main3 extends React.Component{
  // translate(5vw)
  render(){
    // const main2Style ={
    //   backgroundColor: "green",
    // }
    return <div id = "main3"></div>
  }
}

class Sidebar extends React.Component{
  render(){
    return <div className = "sidebar"></div>
  }
}

class First extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      hover2:false,
      style3:{},
    };
  }

  handleHover(){
    if(!this.state.hover2){
      this.setState({
        hover2:true,
      })
    }

  }

  render(){
    var main3Style ={}
    if(this.state.hover2){
      main3Style ={
        backgroundColor:"green",
      }
    }

    return <div className = "first">
      <Navbar />
      <Main />
      <Main1 />
      <Main2 onhover ={()=>this.handleHover()}/>
      <Main3 style={main3Style}/>
      <Sidebar />
    </div>
  }
}

class Second extends React.Component{
  render(){
  return <div className = "second"></div>
  }
}

function App() {

  return (
    <>
    <First />
    <Second />
    </>
  )
} 

export default App;
