import React from 'react';


class Navbar extends React.Component{
  render(){
    return <div className = "navbar"></div>
  }
}

class Main extends React.Component{
  render(){
    return <div id = "main"></div>
  }
}

class Main1 extends React.Component{

  render(){
    return <div id = "main1" onMouseOver={()=>this.props.onMouseOver()} onMouseLeave={()=>this.props.onMouseLeave()} style={this.state.style1}>
      <span id = "main1text" style={this.state.style2}>"How are you?"</span>
      </div>
  }
}

class Main2 extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      origin:true,
      style1:{},
      style2:{},
    };
  }

  changeStyle = ()=>{
    var trans = 'scale(1)';
    this.setState({style1: {transform:trans, zIndex:5}, style2:{opacity:1},origin:false});
  }

  resetStyle =()=>{
    var trans = 'scale(1.25) rotate(36.85deg) translate(0,5vw)';
    this.setState({style1: {transform:trans, zIndex:1}, style2:{},origin:true});
  }


  handleMouseOver(){
    this.timeID = setTimeout(this.changeStyle, 1000);
  }

  handleMouseLeave(){
    clearTimeout(this.timeID);
    if(!this.state.origin){
      this.resetStyle();
    }
  }

  render(){
    return <div id = "main2" onMouseOver={()=>this.handleMouseOver()} onMouseLeave={()=>this.handleMouseLeave()} style={this.props.style1}>
      <span id = "main2text" style={this.props.style2}>"How are you?"</span>
      </div>
  }
}

class Main3 extends React.Component{
  // translate(5vw)
  render(){
    // const main2Style ={
    //   backgroundColor: "green",
    // }
    return <div id = "main3"></div>
  }
}

class Sidebar extends React.Component{
  render(){
    return <div className = "sidebar"></div>
  }
}

class First extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      origin:true,
      style1:{},
      style2:{},
    };
  }

  changeStyle = ()=>{
    var trans = 'scale(1)';
    this.setState({style1: {transform:trans, zIndex:5}, style2:{opacity:1},origin:false});
  }

  resetStyle =()=>{
    var trans = 'scale(1.25) rotate(36.85deg) translate(0,0)';
    this.setState({style1: {transform:trans, zIndex:0}, style2:{},origin:true});
  }

  handleMouseOver(){
    this.timeID = setTimeout(this.changeStyle, 1000);
  }

  handleMouseLeave(){
    clearTimeout(this.timeID);
    if(!this.state.origin){
      this.resetStyle();
    }
  }

  render(){
    return <div className = "first">
      <Navbar />
      <Main />
      <Main1 onMouseOver={()=>this.handleMouseOver()} onMouseLeave={()=>this.handleMouseLeave} style1={this.state.style1} style2={this.state.style2}/>
      <Main2 />
      <Main3 />
      <Sidebar />
    </div>
  }
}

class Second extends React.Component{
  render(){
  return <div className = "second"></div>
  }
}

function App() {

  return (
    <div>
    <First />
    <Second />
    </div>
  )
} 

export default App;
